INSERT INTO FAMILY VALUES (NULL, 1,'Saxifragaceae', '216::217', 'Often succulent herbs found in temperate & cold regions. Name means "rock breaker", as these plants are often found growing among rocks in mountains. Flowers similar in structure to members of rose family, but number of stamens reduced to 4-5, or 8-10, and carpels 1-3. Fruit is a capsule or berry.', ' ');
