INSERT INTO FAMILY VALUES (NULL, 1,'Convolvulaceae', '226::227', 'Large family of herbs (mostly twisting), many are climbers. Petals form a funnel-shaped corolla; 5 stamens attached inside. Ornamental species highly priced for their showy, bright-colored flowers lasting but a day each.', ' ');
