INSERT INTO FAMILY VALUES (NULL, 1,'Cannabaceae', '234::235', '2 genera, 5 species. Herbs. No latex. Leaves palmately lobed, opposite. Flowers dioecious. Male flowers with 5 sepals, 5 stamens. Females with 2 petals but one usually degenerated.', ' ');
