INSERT INTO FAMILY VALUES (NULL, 1,'Cupressaceae', '296::297', 'World: 20 genera, about 145 species; Vietnam: 5 genera, 6 species.<br><br>Evergreen trees or shrubs, monoecious or dioecious. Leaves DECUSSATE (arranged in pairs at right angle to those above and below) or whorl. Scale-like cone woody, coriaceous or berry-like.<br><br>Distr.: wide both hemispheres.', ' ');
