INSERT INTO FAMILY VALUES (NULL, 1,'Sonneratiaceae', '411::412', 'Trees. Leaves opposite or whorled, simple, entire, coriaecous, exstipulate. Flowers solitary or in terminal cyme, large, showy, regular; 4-8 sepals, valvate; 4-8 or no petals, crumped in buds; styles bents in bud. Fruit a berry or capsule, dehiscing loculidally.<br><br>Habitat: mangroves and rain forests.', ' ');
