INSERT INTO FAMILY VALUES (NULL, 2,'Ophiocephalidae', '750::751', 'Also often known as family ''Channidae''. Body elongated, cylindrical near head, flat from side to side near tail. Scales all over body. Dorsal & anal fins long, no hard spine.', ' ');
