INSERT INTO LOCALES VALUES (NULL, 'vn', 'CÃ¡ nhá»“ng');
INSERT INTO FAMILY VALUES (NULL, 2,'Sphyraenidae', '790::791', 'Body long, nearly cylindrical. Lateral line well-developed, relatively straight. Head pointed, scales on top and 2 sides. Snout pointed. Mouth wide, lower jaw protrudes; teeth large but height inconsistent. Pectoral short, low. 1st dorsal fin V, above pelvic fin. 2nd dorsal apart from the 1st, similar to & symmetrical with anal fin. Caudal fin lobed. Distr: wide, temperate & tropical seas, near estuaries of large rivers.', ' ');
INSERT INTO LOCALES VALUES (NULL, 'vn', 'Ca dua / Ca lac');
INSERT INTO FAMILY VALUES (NULL, 2,'Muraenesocidae', '792', 'Similar to family ''Congridae'', distinguished by teeth. Body long, eel-like, nearly cylindrical near head, flat from side to side naer tail. Anus in anterior half of body. Dorsal fin starts above or anterior to pectoral fin, fused with caudal and anal fin. Pectoral fin well-developed. Head long, mouth very broad. Eyes large, oval, hidden under skin. TONGUE NOT FREE. No scale. Lateral line exists. Only 1 genus: ''Muraenesox''. Distr: wide SE Asia.', ' ');
INSERT INTO LOCALES VALUES (NULL, 'vn', 'CÃ¡ chÃ¬nh biá»ƒn');
INSERT INTO FAMILY VALUES (NULL, 2,'Congridae', '793', 'Similar to family ''Muraenesocidae'', distinguished by teeth. Body long, eel-like, nearly cylindrical near head, flat from side to side naer tail. Dorsal fin starts above or sometimes behind pectoral fin, fused with caudal and anal fin. Head long, mouth very broad. Eyes large, oval, hidden under skin. TONGUE FREE. No scale. Lateral line exists.', ' ');
