INSERT INTO FAMILY VALUES (NULL, 2,'Ducula', '803', 'Large, robust pigeons, with broad, rounded wings and slow wingbeats in flight. Sometimes seen in small groups, but much less gragarious than green pigeons. Fruit-eating; strictly arboreal, usually found in the high canopy. Deep, booming calls.', ' ');
