INSERT INTO FAMILY VALUES (NULL, 1,'Aristolochiaceae', '', 'Mostly vines. Leaves alternate, usually with open base. Flowers peculiarly shaped with tubes usually bent. Habitat: mostly warm regions.', ' ');
INSERT INTO FAMILY VALUES (NULL, 1,'Averrhoaceae', '', 'Small trees. Leaves spiral, impari-pinnate; pinnae opposite or alternate. Flowers small; sepals 5; petals 5; stamens 10, free. Fruit a berry, 5-lobolate. Seeds arillate or not.', ' ');
INSERT INTO FAMILY VALUES (NULL, 1,'Ehretiaceae', '', 'Trees or shrubs, rarely subshrubs or woody herbs. Sometimes spiny. Leaves alternate, rarely subopposite. Flowers in cymes, or cylindrical spikes, or in heads; calyx tubular or campanulate, sometimes membranous, enlarged and inflated in fruit; corolla tubular, 5- (rarely 4- or 6- or more) lobed; stamens as many as and alternate with corolla lobes. Fruit a drupe.', ' ');
INSERT INTO FAMILY VALUES (NULL, 1,'Gesneriaceae', '', 'Chiefly herbs and creepers. Stems watery. Leaves opposite, simple, frequently colored above or underneath and hairy. Flowers usually showy.<br><br>Habitat: chiefly moist tropics.', ' ');
INSERT INTO FAMILY VALUES (NULL, 1,'Goodeniaceae', '', 'Herbs or undershrubs. Leaves alternate, rarely opposite. Flowers perfect, solitary to paniculate; calyx tubular, mostly 5-lobed; corolla sympetalous, 5-merous; stamens 5. Druit a drupe or nut-like, or capsule. Seeds small.', ' ');
INSERT INTO FAMILY VALUES (NULL, 1,'Heliconiaceae', '', 'Herbs with rhizomes. Leaf sheath usually open and with a LIGULE (a thin membrane attached to the leaf at the point where the blade meets the leaf sheath. Inflorescence terminal; calyx & corolla whorls'' bracts 3-30 per inflorescence, showy; sepals 3 (2 fused to corolla except at apex); petals 3, more or less inborne; stamens 5-6. 1-3 seeded drupe, often arillate.', ' ');
INSERT INTO FAMILY VALUES (NULL, 1,'Hypoxidaceae', '', 'Perennial herbs, with an erect or tuberous subterraneous rhizome.  Leaves lanceolate or linear-lanceolate or linear. Inflorescence a raceme or a spike; petals 6, free, yellow; stamens 6; stigmas 3. Fruit a capsule or berry.', ' ');
INSERT INTO FAMILY VALUES (NULL, 1,'Lycopodiaceae', '', 'Fern allies. Evergreen terrestrial or epiphytic herbs. Stem erect, postrate or pendulous, usually dichotomous branches. Leaves scale-like or needle-like; 1-veined, spiral, imbricate or subverticillate. Sporophyll terminal in strobilus; axillary sporangium, homosporous, Gametophytes with or without chlorophyll.', ' ');
INSERT INTO FAMILY VALUES (NULL, 1,'Menyanthaceae', '', 'Aquatic or marsh herbs. Leaves simple or compound, spirally arranged; leaf sheath at base. Flowers bisexual, regular, solitary or various inflorescence types. Calyx of 5 or inborne sepals; corolla of 5 fused petals, usually crested or fringed margin. Filaments attached to the tube. Fruit a capsule or a berry.', ' ');
INSERT INTO FAMILY VALUES (NULL, 1,'Sambucaceae', '', 'Shrubs, sometimes lianous herbaceous. Leaves opposite, usually simple, rarely pinnate. Inflorescence cymose or its modification, usually bisexual; calyx 5-lobed, usually small; corolla 5-lobed; stamens 4 or 5. Fruit a berry or a drupe.', ' ');
INSERT INTO FAMILY VALUES (NULL, 1,'Schizaeaceae', '', 'Ferns, often climbing. Leaves simple or pinnate, some thread-like, others palmate. Bear spores.<br><br>Habitat: mainly tropics.', ' ');
INSERT INTO FAMILY VALUES (NULL, 1,'Taccaceae', '', 'Herbs with fleshy roots and basal leaves. Green or brownish-purple flowers with long thread bracteole between.', ' ');
INSERT INTO FAMILY VALUES (NULL, 1,'Zygophyllaceae', '', 'Annual or perennial herbs, undershrubs or shrubs, rarely trees. Leaves opposite, rarely spirally arranged, 2-3 foliate or pinnate. Flowers solitary or in cymose, bisexual; sepals 4-5; petals mostly 4-5; stamens usually twice as many as petals. Fruits mostly a capsule.', ' ');
INSERT INTO FAMILY VALUES (NULL, 1,'Symphoremataceae', '', 'Herbs, shrubs or trees. Stems or twigs often quandrangular. Leaves usually opposite or whorled, mostly simple. Inflorescence variable, usually determinate. Flowers typically bisexual or polygamous by abortion; calyx typically 5-lobed; corollar usually with as many lobes as calyx; stamens primitively 5; pistil 1. Fruit generally a drupe.', ' ');
INSERT INTO FAMILY VALUES (NULL, 1,'Strychnaceae', '', 'Herbs, shrubs or trees. Leaves opposite. Flowers bisexual; calyx 4-5 lobed or parted; corolla gamopetalous 4-5 or 10 lobed; stamens as many as corollar lobes', ' ');
INSERT INTO FAMILY VALUES (NULL, 1,'Strelitziaceae', '', 'Perennial or annual herbs, either stemless and clump forming or bearing multiple start, or simple stem. Leaves large, in ranks, resembling banana leaves but more rigid, oblong to lanceolate.', ' ');
