INSERT INTO GENUS VALUES (NULL, 1,'Chukrasia', '', 'Filaments connate into a cylindrical tube; no disk; ovules many in each cell; capsule 3-celled. Seeds packed transversely in each cell, winged.', ' ', ' ');
INSERT INTO GENUS VALUES (NULL, 1,'Croton', '', 'Leaves usually have 2 glands at base, often with a covering of stellate scales on tomentum when young; some leaves 3- to 5-ribbed at base.', ' ', ' ');
INSERT INTO GENUS VALUES (NULL, 2,'Merops', '', 'Gregarious, graceful flyers; catch insects from exposed perches in open country; have liquid call notes "quilp, quilp"'' have black line through eye and coopery underwing.', ' ', ' ');
