INSERT INTO GENUS VALUES (NULL, 2,'Larus', '984::985', 'Large, robust seabirds with broad-based wings, usually rounded tails, slightly hooked bills. Feed mostly on fish & offal. Larger species take 4 years to reach maturity; immature plumages can be confusing.', ' ', ' ');
