INSERT INTO GENUS VALUES (NULL, 2,'Columba', '986::987', 'Moderately large, heavily built, but smaller than imperial pigeons (genus ''Ducula'') with more pointed wings. Most feed arboreally on fruit.', ' ', ' ');
INSERT INTO LOCALES VALUES (NULL, 'en', 'Pigeon, Imperial');
INSERT INTO GENUS VALUES (NULL, 2,'Ducula', '988', 'Large, robust, with broad, rounded wings and slow wingbeats in flight. Sometimes seen in small groups but much less gregarious than green pigeons (genus ''Treron'').  Fruit-eating; strictly arboreal, usually found in high canopy. Deep, booming calls.', ' ', ' ');
INSERT INTO LOCALES VALUES (NULL, 'en', 'Pigeon, Green');
INSERT INTO GENUS VALUES (NULL, 2,'Treron', '989', 'Though readily told from other pigeons by their predominantly green body, most are confusingly familiar, particularly the females. Feets red in all but 2 species. Yellow or whitish wing bars usually conspicuous, underwings grey. Strictly arboreal, often difficult to observe in forest canopy. Often found in large flocks in fruiting fig trees. Many species persistently pump their tails up and down. Have soft, musical whistling calls.', ' ', ' ');
