INSERT INTO GENUS VALUES (NULL, 1,'Eurostopodus', '990', 'This genus is now sometimes placed in a separate family, ''Eurostopodidae''.', ' ', ' ');
INSERT INTO GENUS VALUES (NULL, 1,'Yucca', '', 'This genus is also classified in family ''Agavaceae''.', ' ', ' ');
