INSERT INTO GENUS VALUES (NULL, 2,'Rasbora', '991', '20-30 cm. Unlike other genera in the family: NO WHISKER. Anal fin has only 5 rays. Habitat: tropical freshwater like in S China, SE Asia. Not economically important.', ' ', ' ');
INSERT INTO GENUS VALUES (NULL, 2,'Schizothorax', '', 'Common, widely distributed. Scales all over body. Mouth under the head. Lower lip continuous, large. About 20 species in China.', ' ', ' ');
INSERT INTO LOCALES VALUES (NULL, 'vn', 'CÃ¡ he / CÃ¡ Ä‘ong');
INSERT INTO GENUS VALUES (NULL, 2,'Puntius', '992', 'About 8 species in China, 15 in S and 3 N Vietnam. Complex classification. Small-sized. Snout usually short, truncate. Mouth at anterior, narrow. Lips thin, close together. Lower lip fused with lower jaw. Lower jaw not keratinized. Dorsal fin 8, last spine usually serrate. Single ray at end of anal fin NOT SERRATE.  Distr. wide Africa, India, Indonesia, S China, Vietnam.', ' ', ' ');
INSERT INTO GENUS VALUES (NULL, 2,'Leiocassis', '', 'The largest genus of the family ''Bagridae''. Similar to the genus ''Pelteobagrus''. Not specific features to separate this genus from the others.', ' ', ' ');
INSERT INTO LOCALES VALUES (NULL, 'vn', 'Khat nuoc');
INSERT INTO GENUS VALUES (NULL, 1,'Clamator', '993', 'Entirely parasitic: lay their eggs in other birds'' nests and do not participate in raising the young. Arboreal. Feathers cover front parts of thighs. Have crest on crown.', ' ', ' ');
