INSERT INTO GENUS VALUES (NULL, 2,'Eudynamys', '995', 'Entirely parasitic: lay their eggs in other birds'' nests and do not participate in raising the young. Both young and host''s young live together in nest. Arboreal. Feet not covered by feathers. Tail and wings of nearly equal length.', ' ', ' ');
INSERT INTO LOCALES VALUES (NULL, 'en', 'Cuckoo');
