INSERT INTO GENUS VALUES (NULL, 1,'Piper', '1006::1007', '700+ species. Shrubs, often climbing, rarely trees or herbs. Leaves stipulate; venation scattered comspicuously. Flowers in terminal spikes, pushed aside & leaf-opposde by development of axillary bud.', ' ', ' ');
INSERT INTO LOCALES VALUES (NULL, 'vn', 'Rau cÃ ng cua');
