INSERT INTO GENUS VALUES (NULL, 2,'Petaurista', '1089::1090', '5 species, all in Asia, most of SE Asia. Together with genera ''Aeromys'', ''Hylopetes'', Petinomys'' and ''Belomys'', is classified in subfamily ''Petauristinae'' which is also considered a separate family ''Petauristidae''. Very large, with a gliding membrane connecting hind legs to tail base. Can glide 400-500 m from a tall tree overlooking a valley. Tail round, not flattened as in many other squirrels. Mainly nocturnal. Forage fruit & shoots singly or in small groups. Female has 3 pairs of mammae. Litter size: 1-2. Logest recorded lifespan in captivity: 13.5 years. Habitat: normally dense, highland forests. All species in Thailand are classified as "Protected, Schedule 1": hunting or keeping in captivity is allowed only under license.', ' ', ' ');
INSERT INTO GENUS VALUES (NULL, 2,'Hylopetes', '', '6 species in Asia. Together with genera ''Aeromys'', ''Petaurista'', Petinomys'' and ''Belomys'', is classified in subfamily ''Petauristinae'' which is also considered a separate family ''Petauristidae''. Four species in Thailand (little known) very difficult to distinguish in the field, even museum specimens can be identified often by resorting to examination of the skull. Upperparts from grey to bright red-brown; underparts whitish, greyish or yellowish. Tail markedly flattened, feather-like. Female has 3 pairs of mammae. Distr: usually northern areas: Nepal, Burma, N Thailand. Habitat: forests 150-3,500 m. All species in Thailand are classified as "Protected, Schedule 1": hunting or keeping in captivity is allowed only with license.', ' ', ' ');
INSERT INTO GENUS VALUES (NULL, 2,'Belomys', '', 'Only 1 species in this genus. Together with genera ''Aeromys'', ''Hylopetes'', Petinomys'' and ''Petaurista'', is classified in subfamily ''Petauristinae'' which is also considered a separate family ''Petauristidae''.', ' ', ' ');
INSERT INTO GENUS VALUES (NULL, 2,'Tamiops', '', 'Resemble the New World chipmunks in having dorsal stripes amd a marrow, thinly-haired tail, but arboreal, unlike the primarily terrestrial chipmunks. Characterized by 5 black stripes (outer pair often indistinct) alternating with 4 light stripes on dorsum. Females have 3 pairs of mammae. Distr: most of South Asia. Habitat: often in mountainous forests, also on lower elevations in some areas and plantations.', ' ', ' ');
INSERT INTO GENUS VALUES (NULL, 2,'Ratufa', '', 'Very large; tail relatively long (longer than H+B) and somewhat flattened. Diurnal, arboreal. Distr: most of SE Asia, also S Asia. Habitat: forest canopy. Both species in Thailand ''R. bicolor'' (darker) and ''R. affinis'' (lighter) are classified as "Protected, Schedule 1": hunting or keeping in captivity is allowed only with license.', ' ', ' ');
