INSERT INTO GENUS VALUES (NULL, 2,'Elaphe', '1158', 'Teeth on upper jaw may be enlarged. Head rather distinct from neck; pupil round. Terrestrial, although good climbers. Oviparous. Diet: mammals, birds.', ' ', ' ');
