INSERT INTO GENUS VALUES (NULL, 2,'Hydrophis', '1160::1161', 'SE Asia: 15 species. Vary considerably in form and structure. All characterized by small ventrals, enlarged regular head shields, large anterior chin shields, and triangular mental.', ' ', ' ');
INSERT INTO GENUS VALUES (NULL, 1,'Arachnis', '', 'Generally large plants with monopodial structure. Stem of some species may be as long as 3-4m. Leaves linear or ligulate, coriaceous, stiff, alternate, well spaced. Inflorescence axillary, still, fairly long, branched or simple. Flowers in variable number, waxy, long-lasting, opening in succession. Certain species are interbred to obtain cut flowers. Sepals often with red or purple cross-stripes on greenish background; sepals and petals well apart from each other, often narrow at base, enlarged at tip.<br>Origin: Philippines, Solomon Islands, New Guinea, SE Asia. Habitat: hot and very humid climate, so seldom flower outside countries of their origin although easy to grow.', ' ', ' ');
INSERT INTO GENUS VALUES (NULL, 1,'Ascocentrum', '', 'Medium or medium-small, with monopodial structure. Leaves linear, stiff, alternate, carinate, not more than 25-30 cm long, may have 2 teeth at apex, cover strem base. Inflorescence axillary, sometimes more than one at a time, erect, more or less as long as leaves. Flowers many, 1-3cm, very close together, opening in succession, with a spur, flat, conspicuous, long-lasting, brightly colored in yellow, orange-red, scarlet or purple. Hybridized with ''Vanda'' (=''Ascocenda'') many to reduce dimensions of the latter, also to increase flowering capacity and color range. Also crossed with ''Neofinetia'' (=''Ascofinetia'') and ''Phalaenopsis'' (=''Asconopsis'').', ' ', ' ');
INSERT INTO GENUS VALUES (NULL, 1,'Ascocenda', '', 'This is, in fact, a hybrid genus resulting from the crossing between two genera: ''Ascocentrum'' and ''Vanda''. The purposes are to reduce the dimensions of ''Vanda'', increase flowering capacity and improve color range.', ' ', ' ');
