INSERT INTO GENUS VALUES (NULL, 2,'Motacilla', '1164', 'Some 10 species. Strongly patterned. Incessantly WAG their long tails up and down. Distr.: Old World. Habitat: beaches, meadows, and streamsides; usually nest on the ground but roost in trees.', ' ', ' ');
INSERT INTO GENUS VALUES (NULL, 2,'Terpsiphone', '', 'Syn.: ''Tchitrea''. About 10 species, but taxonomy extremely confused because of geographical and individual variation. Many have crests and eye wattles, and breeding males of some species have elongated tail feathers, which may be 40 cm long. Plumage colours are usually combinations of glossy black, white, and rufous brown. Distr.: tropical Africa and Asia, north through eastern China and Japan.', ' ', ' ');
