INSERT INTO GENUS VALUES (NULL, 2,'Dicaeidae', '1167::1168', '47 species. Largest 23 cm. Have a double-tubed and brush-tipped tongue and finely serrated bill. Flit about, twittering, in trees and shrubs where they find small fruits. The pouch-like, felted nest may have a porched side entrance. Distr.: S & SE Asia, western Pacific islands, Australia.', ' ', ' ');
INSERT INTO GENUS VALUES (NULL, 1,'Bulbophyllum', '', 'One of the largest genera of orchids, composed of more than 1,000 species of African and Asian plants with creeping rhizomes and rounded pseudobulbs (bulb-like stems). Most species have a single leaf per pseudobulb. The sepals are coloured and are larger than the petals. Many of these orchids have small flowers and an offensive odour.', ' ', ' ');
