INSERT INTO GENUS VALUES (NULL, 2,'Myiophoneus', '1179::1180', 'Large, dark bluish thrushes with glossy spangles in plumage. Inhabit moist, shady forests, rushing streams. Shy; loud, piercing calls. Take many aquatic invertebrates, and snails. Build cup-shaped nests in crevices of banks or fallen trees.', ' ', ' ');
