INSERT INTO GENUS VALUES (NULL, 2,'Zoothera', '1181', 'Medium to large. Rather shy birds of wooded habitats. Bound along the forest floor, feeding on insects, worms or fallen fruit. Many also take fruit and occasionally nectar from the tree-tops.<br><br>The species of this genus has broad white bands on the underwing, which are conspicuous in flight. Usually solitary or in pairs, feeding mostly on the ground and flying up to perch inconspicuously in understorey trees when disturbed. Some are resident.', ' ', ' ');
INSERT INTO LOCALES VALUES (NULL, 'en', 'Thrush, Typical');
INSERT INTO GENUS VALUES (NULL, 2,'Turdus', '1182', 'Medium to large. Rather shy birds of wooded habitats. Bound along the forest floor, feeding on insects, worms or fallen fruit. Many also take fruit and occasionally nectar from the tree-tops.<br><br>The species of this genus are exclusively winter visitors in Thailand. Many are irruptive in occurrence, large numbers occuring in some years with comparatively few in others. Often in mixed flocks which may comprise 2 or 3 species, feeding in frliiting trees or shrubs, or on the forest floor.', ' ', ' ');
