INSERT INTO GENUS VALUES (NULL, 2,'Rhinomyias', '1183', 'Brownish plumage with rufescent tails. Rounded heads, long and heavy bills, comparatively short wings and moderate length tails which frequently held cooked. In shape and actions resemble female Blue Flycatchers (Cyornis spp.) and, like them, inhabit middle storey and understorey of forest. Sexes similar.', ' ', ' ');
