INSERT INTO "NaturalObject" VALUES(82,1,119,'11::18',369,79,'','vn vn (N)::vn vn xanh (S)',103,'06/06/1999 09:29:29',NULL,'Ho (1970) v.1 p.311','30 m, hairy when young. Leaves oblong, not hairy above; petioles 1.5 cm, not hairy when old; stipules obtuse, hairy. Racemes terminal or axillary; sepals much hairy outside; petals 5-9 cm, white; stamens about 25, long-cuspidate. Distr. Vietnam: Song Be, Dong Nai.',NULL);
