INSERT INTO "NaturalObject" VALUES(85,1,362,9,124,81,'','tin to bn',103,'23/03/1997 09:29:29',NULL,'Ph. H. H.','Largest of the genus in Vietnam: 10m. Leaves very large: up to 3 m; tri-pinnate. Petioles 1m, BROWN UNDERNEATH, paler or YELLOW ABOVE. Rachis and rachules STRAW COLORED, smooth, very few or no hairs; punules DEEPLY LOBED up to rachule; yellow-brown beneath when dry. Spores located near rachules. Habitat: forests usually below 800 m.',NULL);
INSERT INTO "NaturalObject" VALUES(86,1,29,'28::18',371,82,'','lau','187::94','30/08/1998 09:29:29',NULL,' ','2-7 m. RESEMBLES SUGARCANE. Stem solid, 2-3 cm. Sheaths have no hair, margins hairy; lamina large, 2 m by 2-5 cm. Panicles LONG up to 1 m; spikes have many white hairs shorter than spikes; 2 flowers. Habitat: moist soils, near water.',NULL);
INSERT INTO "NaturalObject" VALUES(87,1,251,'28::29',372,83,'pennywort, India','rau m','92::188::10::5::28::94','28/01/1996 09:29:29',NULL,' ','Creeps close to ground surface. Another source puts under family ''Umbelliferae''. Leaves stalked, reniform or obicular, crenate, smooth. Flowers stalked, in umbels, pinkish. Fruits oblong, curved, brown. Leaves used as refreshment.',NULL);
INSERT INTO "NaturalObject" VALUES(88,1,50,'28::5',373,70,'water lily, blue','sng xanh','189::149::94','16/04/1995 09:29:29',NULL,' ','Leaves peltate, orbicular or elliptic, sinuate, serrate or entire, glabrous. Flowers blue or white.',NULL);
