INSERT INTO "NaturalObject" VALUES(105,1,182,'28::18',383,98,'','gip c::dic c','92::9::5::36::94','29/02/1904 21:33:01',NULL,'Ho (1999) #1154','Perennial, 5-50 cm. Leaves have special fragrance, sour, 7-veined. Racemes OPPOSITE TO LEAVES, 4 petals white, 1-3 cm. Probably used to treat hemorrhoid.',NULL);
