INSERT INTO "NaturalObject" VALUES(398,1,29,28,559,361,'','c ch::tc hnh',362,'16/03/1995 09:29:29',NULL,'Ho (1970) v.2 p.937','Annual. Stems creeping then erect. Leaves 2,5-4 mm across by 3-5 cm long. Inflorescence consists of TWO LONG BRANCHES 3-7 cm; spikes IN PAIRS one with a long stalk one without. Habitat: roadsides, dry soils.',NULL);
