INSERT INTO "NaturalObject" VALUES(400,1,10,'11::18',26,363,'golden shower::amaltas','b cp nc::mung hong yn','92::363::364::9::149::31::16::45::26::94','14/07/1999 09:29:29',NULL,'Ho (1970) v.1 p.824','Synonym tentative. 10-20 m, semi-deciduous. Leaves unipinnate, 30-60 cm long, 3-8 pairs of pinnae, 12x6 cm, ovate, slightly leathery, acuminate. Racemes DENSE ALL OVER TREE, pointed, pendulous; petals BRIGHT YELLOW. Pods 30 cm, cylindrical, 2-3 cm diameter, smooth, hard, dark brown. Distr: tropical Asia: wide; Thailand as ornamental & shade (shown in Thai stamps). Wood used for building, carts, fence posts... Pods used as laxative. Can grow in poor shallow soils, resistant to drought, tolerant to shade, coppices vigorously, produces root suckers.',NULL);
INSERT INTO "NaturalObject" VALUES(401,2,568,'37::15',560,364,'prawn, giant tiger','tm s','92::365::9::52::54::94','30/08/1999 09:29:29',NULL,'Chung & Du (1995)','11-33 cm. Body green-black, with broad wide vertical stripes. Legs red-black. Habitat: blackish. Distr.: Africa, Pakistan to Thailand, Malaysia, N Australia, Japan; Vietnam: C&S very common. Cultured extensively (far more than other species) in Thailand, Vietnam.',NULL);
INSERT INTO "NaturalObject" VALUES(402,2,506,'37::15',560,365,'prawn, banana::prawn, white','tm bc th','116::366::9::52::124','30/08/1999 09:29:29',NULL,'Suthin; Chung & Du (1995)','15-20 cm. Yellowish, numerous blue small spots. Distr.: Persian Gulf to Thailand, N Australia, Indonesia, Hong Kong; Vietnam: C&S common. Cultivated in extensive farms in Thailand, with natural seeds and feeds, 2nd to ''P. monodon''; also in Vietnam.',NULL);
INSERT INTO "NaturalObject" VALUES(403,2,569,'26::12',561,366,'prawn, giant freshwater::prawn, giant river','tm cng xanh','92::10::9::52::54::94','30/08/1999 09:29:29',NULL,'Chung & Du (1995)','13-30 cm. Green. Distr.: India to Thailand, Philippies, Australia, New Guinea; Vietnam: very common. Habitat: freshwater, brackish and sometimes marine. Extensively cultivated.',NULL);
