INSERT INTO "NaturalObject" VALUES(415,2,244,'2::15',571,378,'mackerel, Indo-Pacifc::mackerel, short-bodied','c bc m','92::376::10::52::54::94','11/05/1995 09:29:29',NULL,' ','18-50 cm. Extensively caught off Thai fishermen, a part of Thai staple meal.',NULL);
INSERT INTO "NaturalObject" VALUES(416,2,244,'2::15',179,379,'mackerel, narrow-barred Spanish','c thu o::c thu vch','92::377::10::124','29/08/1999 09:29:29',NULL,' ','Similar to ''S. guttatus'' but body has grey vertical BARS across lateral line. Exploitable 40-60 cm, 0.4-1.5 kg. Fast growing: 10 cm at 4 mo, 20-30 cm 4 mo. Body slender, gradually tapers at head and tail. Carnivore. Spawns Feb-Jun in shoals. Habitat: open tropical seas, near surface. Distr: Indian to Malaya, S China and China. Meat tasty. Most common among Spanish mackerel species, constituting about 80% of Spanish mackerel catch in Thailand; high yields off C coasts of Vietnam.',NULL);
INSERT INTO "NaturalObject" VALUES(417,2,244,'2::15',179,380,'mackerel, Spanish::mackerel, Indo-Pacific king','c thu ?','92::378::10::52::124','23/06/1997 09:29:29',NULL,' ','30-100 cm. Body long, symmetrically elliptic, blue-grey above, whitish below, large round grey dots on flank. One of three Spanish mackerel species in Gulf of Thailand.',NULL);
INSERT INTO "NaturalObject" VALUES(418,2,244,'2::15',179,381,'mackerel, Spanish','c thu ?','92::379::10::124','28/01/1995 09:29:29',NULL,' ','One of three Spanish mackerel species in Gulf of Siam.',NULL);
INSERT INTO "NaturalObject" VALUES(419,2,'372::570','2::12',158,382,'catfish, striped','c tra yu','92::380::9::52::54::94','11/02/1996 09:29:29',NULL,' ','Up to 1.3 m. Grey above, lighter below. A DARK THIN STRIPE run at mid-hight from gill cover to caudal fin base, interrupted by whitish spaces. Pectoral I,12; ventral 8; dorsal I,7; anal 30-32. Anal fin with a horizontal DARK STRIPE in the middle. Distr: Laos; Thailand: widely cultured in for local consumption and also for export; Vietnam: Tien & Hau R., but rare.',NULL);
INSERT INTO "NaturalObject" VALUES(420,2,215,'2::12',572,383,'carp, bighead','c m hoa::c m en','92::9::120','25/10/1998 09:29:29',NULL,' ','Sub-family ''Cyprininae''. The only species in the genus. Head large, about 1/3 of body length. Dark color. Omnivorous, capable of feeding on algae. Can spawn naturally only in some areas in China, introduced into Vietnam. Important culture species in large water bodies, fast-growing: 0.5 kg in 1 yr. Diet: zooplankton. Meat tasty.',NULL);
INSERT INTO "NaturalObject" VALUES(421,2,296,'2::15',573,384,'milkfish','c sa','92::381::122::9::52::124','11/02/1996 09:29:29',NULL,'RB Vie - Animals (1992)','Max 13 kg, 100 cm; catch size 3 kg. Body rhombus; dark green above, flanks & belly silvery white. Scales small, thin. Snout truncated. Eyes totally covered by fatty eyelids. Mouth small, no tooth. Lateral line straight. Caudal fin very long, deeply lobed close to tail end. Dorsal fin deeply concave. Diet: monocellular algae. Distr: wide Japan to S China, SE Asia to Indian & E Africa; Vietnam: C. Habitat: marine to brackkish, can be reared even in freshwater. Lays 3-4 M eggs Apr-May in sea near coasts; fingerlings washed to blackish swamps to grow up. Cultured widely.',NULL);
INSERT INTO "NaturalObject" VALUES(422,2,394,'2::12',339,385,'catfish, freshwater','c tr trng','119::10::9::52::382','30/06/1997 09:29:29',NULL,'RB Vie - Animals (1992)','Larger than ''A. fuscus'': 0.2 kg at 1 yr, 0.35 kg 3 yrs; max 40 cm. Dorsal region grey-black, other parts lighter. Sometimes white dots on body. D=68-74; P=I,7-9; V=7; A=48-55 (all above same as ''C macrocephalus''). Pectoral fin large, strong, with spine serrate both edges. Dorsal & anal fins long but not continuous with caudal fin. Sometimes a number of black dots on body. 4 pairs of whiskers. Distr: wide Sri Lanka to India, SE Asia; Vietnam: wide southern half. Habitat: ponds, swamps, small canals; spawns in holes on banks. Can be bred artificially.',NULL);
INSERT INTO "NaturalObject" VALUES(423,1,9,'11::18',11,386,'African savanna tree','','92::48::342','25/02/1995 09:29:29',NULL,' ','Not in Ho (1970). Widely planted in India for fodder.  Sheds leaves in the onset of rains and retains leaves during dry season: provides shade for cattle when other trees are leafless, also prevents soil erosion.',NULL);
