INSERT INTO "NaturalObject" VALUES(428,1,307,'28::18',576,361,'star of Bethlehem','l danh','92::9::65::31::339','10/10/1998 09:29:29',NULL,'Ho (1970) v.2 p.499','60 cm. Perennial. Sap very bitter. Leaves 10-17 cm, widely serrate, with fine hairs. Flowers solitary, axillary, white; calyx tube 1 cm, ear 1 cm; corolla tube 8-11 cm, ear 2 cm; stamens 5, attached at middle of corolla. Pods 1.5-2 cm. Seeds many, small.',NULL);
INSERT INTO "NaturalObject" VALUES(429,1,51,'11::4',577,315,'mulberry, common','du tm','92::9::34::35::94','25/04/1995 09:29:29',NULL,' ','When cultivated, usually in form of bushes, but if allowed can grow to medium-sized trees.',NULL);
INSERT INTO "NaturalObject" VALUES(430,1,60,'11::4',578,390,'curry leaf tree','nguyt qui Koenig','92::385::386::348::387::351','29/04/1995 09:29:29',NULL,'Ho (1970) v.1 p.662','3 m, deciduous. Bark thin, dark grown with light colored patches. Leaves impari-pinnate; 11-25 leaflets ALTERNATE, 3 cm, entire, obliquely ovate lanceolate, acuminate, glabrous, strongly scented. Flowers Apr-May, in corymbs, many, white. Fruits Jun-Jul, oval, 1 cm, wrinkled, black when ripe. Leaves used for flavoring curries, also medicinal for dysentery, applied externally for eruptions, in decoction for snake bites.',NULL);
INSERT INTO "NaturalObject" VALUES(431,1,70,'28::18',579,391,'firecracker flower::crossandra, dark-leaved','ho hong','92::388::9::112','26/03/1901 18:32:41',NULL,'Ho (2000) #7938','1 m, evergreen. Stems cylindrical, with fine hairs. Leaves in whorls of 4, elliptic, glossy, margin wrinkled. Spikes quadrangular, terminal, dense, with bracts fused; calyx tubular split into 4; corollar ORANGE-RED; 5 ears; stamens 4. Pods 4-edged. Seeds 4. Origine: India. Entire plant sexual stimulant.',NULL);
