INSERT INTO "NaturalObject" VALUES(440,1,32,'28::18',581,399,'','ta t','92::9::36::94','07/10/1998 09:29:29',NULL,'Ho (1970) v.2 p.393','0.5-1m. Trunks square. Leaves with rough hairs; deep red, fragrant. Flowers opposite at leaf nodes and branchlet ends; petals bell-shaped, 5-toothed, white or purplish; 4 petals not extending outside of flower. Var ''nankinensis'': leaves red, edge wavy. Var ''laciniata'': leaves incised by half. Var ''macrophylla'': leaves large. Var ''variegata'': leaves with white patches.',NULL);
INSERT INTO "NaturalObject" VALUES(441,1,7,'11::18',387,400,'hibiscus, coral','bp x cnh::bp ra','92::9::149::390','30/08/1998 09:29:29',NULL,'Ho (1970) v.1 p.355','4 cm. Leaves elliptic, not hairy, close together on short branches, serrate. Flowers solitary, pedicels 10-15 cm; depals 1.5-2 cm, boat-shaped; petals red, DEEPLY LOBED; stamens long , drooping. Flowers pink; petals divided into small stripes.',NULL);
