INSERT INTO "NaturalObject" VALUES(456,1,145,'11::18',58,412,'mahogany','xa c','394::9::16::45::94','30/05/1999 09:29:29',NULL,'Ho (1970) v.1 p.638','Red-colored timber of average quality. Widely planted along roads to give shade, but large horizontal roots can damagae road foundation.',NULL);
