INSERT INTO "NaturalObject" VALUES(466,1,170,'11::18',590,8,'pomegranate','lu','401::139::94','02/05/1995 09:29:29',NULL,' ','Varieties with double red and double white flowers exist. A dwarf form is ''P. granatum var. nana''. Origin: Meditterranian.',NULL);
INSERT INTO "NaturalObject" VALUES(467,1,8,'11::18',28,421,'rosewood','','92::10::45::85::131','06/05/1995 09:29:29',NULL,' ','Not in Ho (1970). Large, deciduous. Most valuable timber species in the genus. Heartwood dark purple with black streaks. Timber prized for high-class furniture, plywood, house construction.',NULL);
INSERT INTO "NaturalObject" VALUES(468,1,61,'11::18',99,422,'rose apple','mn (S)::roi (N)','283::286','07/02/1995 09:29:29',NULL,' ','Same species as ''Syzyzium javanica'' or ''Eugenia javanica'' ?.',NULL);
INSERT INTO "NaturalObject" VALUES(469,1,7,'11::18',591,423,'','hong manh',103,'30/08/1998 09:29:29',NULL,'Ho (1970) v.1 p.347','1 m, few-branched. Leaves resemble of thyme, hairy unnerneath, less so upper surface, elliptical, serrate. Flowers solitary; pedicels 3-7 cm; sepals small; petals yellowish; 12 carpels. Fruits 2 mm. Habitat: roadsides, wasteland, plains.',NULL);
INSERT INTO "NaturalObject" VALUES(470,1,256,'11::18',592,91,'sandal wood','trm hng','92::10::34::124','27/04/1995 09:29:29',NULL,' ','Small, evergreen, glabrous. Branches slender, drooping. High-priced due to strongly-scented wood esp. when burned.',NULL);
INSERT INTO "NaturalObject" VALUES(471,1,9,'11::18',11,424,'wattle, silver','keo trng','92::9::3::4::311','15/09/1998 09:29:29',NULL,'Ho (1970) v.1 p.800','Closely allied to ''A. nilotica'', up to 10m. Diagnostic: leaves FERN-LIKE, hairy, SILVERY grey or whitish from mealy POWDER; pinnae 10-20, NARROW; pinules 30-40, 3-6 mm, close together, without glands. Racemes erect; flowers pale lemon-yellow, fragrant; in panicles 15-22 cm. Pods more than 1 cm across, NOT CONTRACTED between seeds, whitish. Species name means "white-powdered". Habitat: semi-arid, sub-tropical with cool winters. Distr Vietnam: Da Lat.',NULL);
INSERT INTO "NaturalObject" VALUES(472,1,9,'11::18',11,425,'wattle, screw pod','',103,'25/02/1995 09:29:29',NULL,' ','Not in Ho (1970).',NULL);
INSERT INTO "NaturalObject" VALUES(473,1,29,'28::18',593,426,'grass, elephant::grass, Napier','c ui voi::c voi','185::9::48::94','30/08/1998 09:29:29',NULL,'Ho (1970) v.2 p.931','1-3m, looks like sugarcane. Leaves narrow; edge sharp. Cultivated as fodder for cattle.',NULL);
INSERT INTO "NaturalObject" VALUES(474,1,134,'11::18',72,299,'walnut::walnut, common','h o::c ch','92::402::9::139::48::5::45::94','06/05/1901 16:57:37',NULL,' ','30-40 m, deciduous; crown wide spreading. Bark grey, darkening when older; young has long vertical striatons, develop into deep fissures & cracked tranversely into scales with age. Young shoots velvety. Leaves impari, unipinnate, fragrant; 5-13 leaflets, narrow elliptical, almost sessile except terminal one, 7-20 by 5-10 cm. Drupe green yellow-spotted, oblong, 5cm. Nut 2-valved; shell thick, ribbed & wrinkled, much sought after by wildlife. Habitat: 1000-3000m. Leaves useful fodder; both bark & leaves antihelmintic; timber good. Distr.: Nepal, Vietnam: N.',NULL);
INSERT INTO "NaturalObject" VALUES(475,1,177,'11::18',594,427,'willow, weeping','liu','92::9::65::31::94','14/07/1997 09:29:29',NULL,' ','Deciduous, with spreading branches & drooping branchelets. Bark fissured, rough, ridges. Leaves 8-18cm by 1 cm, acuminate, finely serrate; can be used as fodder. Catkins slender, 2-3 cm, pale yellow, drooping, short-stalked. Useful for stabilizing stream banks and dam sites, and for planting up swampy areas.',NULL);
INSERT INTO "NaturalObject" VALUES(476,1,177,'11::18',594,205,'willow, white','liu trng','92::403','19/03/1995 09:29:29',NULL,' ','Deciduous, tall, with a broad outline. Bark deeply cracked with age. Leaves 0.3-1.2 m, thickly covered with silvery silky hairs on both sides, making entire foliage straw color.',NULL);
INSERT INTO "NaturalObject" VALUES(477,1,9,'11::18',11,428,'acacia, gum arabic::gum arabic tree','keo A rp','92::9::69::45::3::4::322','15/09/1998 09:29:29',NULL,' ','Not in Ho (1970). Diagnostic: thorns short, mostly in 3 at swollen nodes. 6-12 m, crown flattened. Trunk short, usually branched near base. Bark pale brown or grey; furrowed, scaly, slash mottled red & white when aged. Leaves bi-pinnate, 2-6 cm; pinnae 3-6 pairs, 2-3 cm; pinnules 8-18 pairs, 3-7 mm, grey-green, hairless. Spikes axillary, appear mostly before leaves, 2-10 cm, cylindrical; calyx 5-toothed, 2-3 mm; corolla 5-lobed, white. Pods short-stalked, oblong, flat. Seeds few, round, flat, 6-10 mm, brown. Good fuelwood, main species for charcoal. Wood whitish, hard.',NULL);
INSERT INTO "NaturalObject" VALUES(478,1,87,'11::18',19,244,'alder, Nepali','tng qua s::tung qua mu','92::404::9::8::3::94','01/06/1999 09:29:29',NULL,'Forestry Inst. v.2','15-20 (Vietnam), 30+m (Nepal), deciduous, fas-growing, nitrogen-fixing. Bark silvery grey, with horizontal patterns of RAISED SWEELINGS, peels off in flakes. Trunk very cylindrical. Crown thin, narrow. Leaves alternate, broadly elliptical, slightly serrate, 4-16x2.5-10 cm; petioles 2-3 cm; base wedged. Dioeceous. Male catkins like squirrel tail, drooping, bunched, 10-25 cm. Female catkins 1-2 cm, axillary. Fruits woody, like fir cones, 2cm, wing 2mm, empty cones remain on trees for long time. Popularly planted for fuelwood and timber. Wood of little use. Used for fodder if nothing else available. Distr.: near streams, ravines, Nepal (with pure stands), N India, Burma, Vietnam NW: Ha Tuyen, Son La, Sapa. Habitat: semi-tropics. Grows fast, pioneer. Germinates & regenerates well. Fixes nitrogen.',NULL);
