INSERT INTO "NaturalObject" VALUES(480,1,26,'11::18',596,430,'ash, mountain::rowan','','92::9::16::65::277','19/04/1995 09:29:29',NULL,' ','Not in Ho (1970). 15+ m, with felted winter buds. Trunk round, smooth. Leaves impari-unipinnate; leaflets 13-15, sessile, elongated lanceolate, sharply serrate, hairy beneath. Flowers creamy white, in wide, dense, flattened, compound corymbs. Fruits dense (several dozens in a bundle), red, berry-like, globose, 1 cm. Habitat: temperate open forests, grassy & rocky slopes from lowlands to mountains.',NULL);
INSERT INTO "NaturalObject" VALUES(481,1,26,'11::18',574,120,'pear::pear, wild','l (S)::x l (N)','92::406::9::139::407','16/04/1995 09:29:29',NULL,' ','Deciduous, 15 m. Crown broadly pyramidal. Branches sometimes thorny. Leaves long-petiolate, ovate, acuminate, finely serrate, glossy. Flowers in rounded corymbs, long-pedicelled, white or rarely pink-flushed, appear before leaves; 5 round petals closely together but not overlapped; anthers pink. Fruits pear-shaped or globose. All cultivars derived from this species, which some authors claim to be of multiple hybrid origin.',NULL);
