INSERT INTO "NaturalObject" VALUES(491,1,7,'11::4',599,438,'cotton, levant','bng vi n','92::9::100','31/08/1998 09:29:29',NULL,'Ho (1970) v.1 p. 359','1.5-2 m. Leaves palmately 3- to 5-lobed (like of maple), ovate-lanceolate, about 10 cm across, entire, acuminate, glabrous. Flowers solitary, axillary. Capsules ovate to globose. Bark to contract uterus, menstruation regulating, enti-pyretic (Cambodia); leaves for cough.',NULL);
