INSERT INTO "NaturalObject" VALUES(493,1,7,'28::18',387,440,'lady''s finger::okra','u bp','92::9::36::54::94','14/11/1996 09:29:29',NULL,' ','Tall, annual plant with brittle hairs. Flowers yellow with crimson center.  Young fruits slimy, used to cook various dishes including curries in Sub-Continent. Unripe seeds used in India as vegetables, in soups and pickles. Ripe seeds have 20% edible oil.',NULL);
