INSERT INTO "NaturalObject" VALUES(499,1,8,'28::18',602,260,'alfalfa::lucerne','','92::9::48::407','15/04/1995 09:29:29',NULL,' ','Perennial, up to 80cm. Stems erect, smooth. Leaves tri-foliate; leaflets narrow obovate, central one distinctly petiolate. Flowers purple, rarely whitish, in dense, short racemes. Pods spirally twisted 2-3 turns. Long cultivated for forage.',NULL);
INSERT INTO "NaturalObject" VALUES(500,1,9,'11::18',11,21,'Egyptian thorn::babul','keo dn::trm hoa vng (N)','92::45::3::4::141','07/07/1995 09:29:29',NULL,' ','Not in Ho (1970). Extremely variables, 9 subspecies eg ''indica'', ''kraussiana''. Diagnostic: thorns paired, straight, light-grey, long, slender; leaves bipinnate, 5-15 cm; leaflets many, narrowly oblong, narrow, grey-green, 1-4 cm; flowers in balls, axillary, tiny, bright yellow; pods narrow, flat, straight, narrow between seeds. Up to 20m, but shrub form in poor soils; nearly evergreen, crown flattened or umbrella-shaped. Most common Acacia species in farm forestry India for charcoal and fuelwood. Yields a gum for glue; tannin obtained from barks. Habitat: arid to semiarid.',NULL);
