INSERT INTO "NaturalObject" VALUES(503,1,11,'28::18',605,250,'dandelion','b cng anh Trung quc','409::9::36::94','25/01/1900 09:29:29',NULL,'Ho (1970) v.2 p.557; Loi (95)','Distr worldwide. Cultivated plants are of special varieties. Roots for blood puration, lactation, abcesses, diuretic.',NULL);
