INSERT INTO "NaturalObject" VALUES(505,1,12,'28::3',231,447,'potato','khoai ty','410::9::36::173::54::35::94','23/11/1996 09:29:29',NULL,'Ho (1970) v.2 p.217','2000+ varieties. Leaves alternate, compound, 1-5 by 0.8-3 cm, mostly ovate, lanceolate, entire, acuminate, pubescent. Flowers in raceme, white to violet. Tibers diuretic, galactagogue, nervous sedative, stimulant in gout.',NULL);
INSERT INTO "NaturalObject" VALUES(506,1,12,'11::4',607,448,'tomato','c chua','411::9::36::54::35::94','23/11/1996 09:29:29',NULL,' ','Var. ''commune'': fruits with ridges; var. ''pyriforme'': fruits pear-shaped.',NULL);
INSERT INTO "NaturalObject" VALUES(507,1,12,'11::4',608,449,'chilly','t','412::9::18::36::54::94','23/11/1996 09:29:29',NULL,'Ho (1970) v.2 p.218','Var. ''acuminatum'' (fing chilly, "ot sung trau"): fruits drooping, 7-10 cm, acuminate, hot. Var ''cerasiforme'' (cherry pepper): fruits erect or drooping, round, hot. Var. ''conoides'' (cone pepper, tabasco): fruit cone-shaped, 2-4 cm, purple. Var. ''fasciculatum'' (red cluster pepper, "ot chi thien"): fruit erect, red, 4-6 cm, hot. Var ''grossum'' (sweet pepper, papikra, "ot bi"): fruits wrinkled, red or yellow, not hot. Var. ''longum'': fruits yellow, long, pointed, slightly hot. Var. ''microcarpum'' (bird pepper, "ot hiem"), fruit red, very small, very hot. Var. ''minimum'' ("ot chi thien"): may be same as ''fasciculatum''.',NULL);
INSERT INTO "NaturalObject" VALUES(508,2,421,'2::15',609,450,'dogshark, yellow','c nhm rng chch::c ngho ngh','92::10::120','26/08/1999 09:29:29',NULL,'Huong (1992)','Exploitable size 40-60 cm, max. not more than 1m. Head very flat, 1/5 of total body length. HORSHOE-SHAPED DEPRESSIONS abbove and below (much smaller) caudal muscle. Blackish above and both sides, white below. Pectoral and anal fins white; other fins brownish-grey. Predator. Gives birth to 10-12 offspring. Habitat: marine, near coasts & in shallow gulfs 20-40 m. Distr.: wide Japan Sea to S China to India; Vietnam: from Do Son to Nam Ha, the most common in the genus in Gulf of Tonkin & Gulf of Thailand. Habitat: warm waters. One of marine fishery target; fins for export.',NULL);
INSERT INTO "NaturalObject" VALUES(509,1,12,'28::18',610,252,'petunia, common garden','c hoa','92::9::31::64::407','26/09/1998 09:29:29',NULL,'Ho (1970) v.2 p.221; Hop (1993); Fanfani (1988)','A hybrid of ''P. axillaris'' & ''P. violacea''. 0.3-1 m. Annual. Branchlets and leaves with long, sticky hairs like tobacco leaves. Leaves ovate, entire, wavery edged, shiny green. Flowers year-round, simple, red, pink or white, or with patches of different colors; pedicels 2-7 cm; sepals 1-2.5 cm, persistent; petals funnel-shaped; 5 stamens attached to lower part of corolla. Origin: South America. Original species: large white petunia ''P. axillaris''; violet-flowered petunia ''P. violacea''.',NULL);
INSERT INTO "NaturalObject" VALUES(510,1,25,'28::18',133,451,'onion, bulb::onion, common','hnh ty::hnh c','413::9::36::414::94','13/06/1995 09:29:29',NULL,'Ho (1970) v.2 p.624','Not found in the wild.',NULL);
INSERT INTO "NaturalObject" VALUES(511,1,25,'28::18',133,452,'chive','','92::9::415','25/03/1995 09:29:29',NULL,' ','Not in Ho (1970). Bright green, grassy and clump-forming plant. Flowers mauve-pink, appear in summer. Leaves mildly onion-flavored, used dried or fresh in many recipes.',NULL);
INSERT INTO "NaturalObject" VALUES(512,2,215,'2::12',611,453,'','c du h::c du','92::10::9::120','30/06/1997 09:29:29',NULL,'Yen et al. (1978)','10 cm. Dorsal fin II,7; pectoral=1,11-12; pelvic=1,8; anal=2-3,14,16. Head small, snout pointed, mouth slightly slanted. Dorsal fin starts slightly behind pelvic fins, with hard spines, clearly serrate. Anal fins short. Caudal fins deeply lobed. Distr China; Vietnam: common in lowland lakes, yield quite high in Ho Tay.',NULL);
