INSERT INTO "NaturalObject" VALUES(517,1,164,'28::25',614,457,'coral vine / pink vine::love''s chain','ti gn::hiu n','92::418::419::9::149::277','16/07/1999 09:29:29',NULL,' ','Vietnamese name is derived from French: antigone. Var. ''albus'' or ''albiflora'': white flowers.',NULL);
INSERT INTO "NaturalObject" VALUES(518,1,24,33,117,458,'spurge','xng rng','92::9::390','25/04/1995 09:29:29',NULL,' ','Stem (phylloclade) green, cylindrical and grooved.  Leaves deciduous, on spirally-arranged ridges having spines. Flowers Mar-May.',NULL);
INSERT INTO "NaturalObject" VALUES(519,1,190,28,386,459,'jute, Nalta::Jew''s mallow','b::ay qu di','92::9::30::35::305','17/06/1995 09:29:29',NULL,'Ho (1970) v.1 p.342','Similar to ''C. capsularis'', but prefers drier soils and has LONG capsules. 1-2 cm, few branched. Leaves broadly lanceolate; 2 ACUMINATE EARS at base; stipules acicular. Flowers axillary, yellow; stamens 45-50; ovaries hairy. Capsules red, up to 7 cm with 10 ridges. Seeds many. Planted, seldom occurs in wild. Leaves enhance lactation, used in soups.',NULL);
INSERT INTO "NaturalObject" VALUES(520,1,69,'28::18',615,260,'hemp, true::hemp, soft','gai mo','92::9::35::30::40::111','10/10/1995 09:29:29',NULL,' ','Leaves with 3- 7-foliate, long petiolate; leaflets slenderly lanceolate, widely serrate, rough. Dioecious, flowers stalked or sessile; male clustered in axillary panicles, perianth 5; female flowers sessile, perianth single. Fruits an achene, enclosed in persistent perianth. Cultivated esp. by Meo ethnic in Vietnam to collect fibers from stems. Seeds pressed to give cooking oil, to prepare prickle in Nepal. Leaves dried to be used as tobacco.',NULL);
INSERT INTO "NaturalObject" VALUES(521,1,75,'28::18',616,460,'','mao v lng',103,'06/09/1998 09:29:29',NULL,'Ho (1970) v.2 p.547','1 m. Leaves alternate, green, 1-5 cm, broadly ovate; few lateral veins not opposite. Spikes axillary, tubular-shaped, 0.5-1.5 cm, APEX ROUND, at branch end: develop at leafless sections. Flowers year-round, bisexual, full of hairs outside; fertile stamens intercated with neuter stamens. Seeds 1. Habitat: wastelands, roadsides at low elevations.',NULL);
