INSERT INTO "NaturalObject" VALUES(913,1,581,1,789,773,'','nm hc th',103,'23/07/1997 09:29:29',NULL,' ','Very small. Bristle. Charcoal black. Hat with stripes, resembling umbrella, easy to spoil & detach from trunk. Very common: lawns, etc.',NULL);
INSERT INTO "NaturalObject" VALUES(914,1,80,1,788,774,'','nm mo (S)::mc nh (N)','646::28::9::54::94','12/03/1995 09:29:29',NULL,' ','Hairs underneath.',NULL);
