INSERT INTO "NaturalObject" VALUES(2516,1,23,'28::25',128,1922,'','::chn rt','92::9::65::94','05/10/1995 09:29:29',NULL,'Ho (1970) v.2 p.718','Very high. Stem squared, 3-4mm, internodes 1.5-3.5cm. Leaves lanceolate, acuminate. Inflorescence axillary, nearly as long as leaves; sheath ovate, light-colored; spadix globose. Berries globose, red, 1cm. Distr. Vietnam: Khanh Hoa, Quang Tri.',NULL);
