INSERT INTO "NaturalObject" VALUES(2531,1,49,'28::18',1439,1558,'','tho qu','92::9::5::415','05/10/1995 09:29:29',NULL,' ','Not in Ho (1970). Rhizomes grow horizontally. Flowers in clusters, developed from base. Fruits deep red; seeds pyramid-shaped, pressed close together, very fragrant. Distr.: wild Ha Tuyen, Hoang Lien Son. Fruits used as spice, also medicinal.',NULL);
INSERT INTO "NaturalObject" VALUES(2532,1,148,'28::18',483,1931,'banana, wild','chui hoa','92::1242','14/07/1999 09:29:29',NULL,'Ho (1970) v.2 p.741','Flower bunch bright red. Fruits with many seeds. Habitat: near water.',NULL);
INSERT INTO "NaturalObject" VALUES(2533,1,133,28,1440,1932,'','sm i hnh::ti l hnh ','92::9::100','05/10/1995 09:29:29',NULL,' ','30 cm. Rhizomes covered by red sheaths. Flowers in racemes, white, borne on long stalk. Rhizomes used to stop bleeding, as antiseptic, and tonic.',NULL);
