INSERT INTO "NaturalObject" VALUES(2542,1,11,'28::18',808,1939,'','cc bc u','92::10::100','06/10/1995 09:29:29',NULL,'Ho (1970) v.2 p.518','Annual, 50-70cm, MANY BRANCHES. Leaves rhomboid, few hairs above, full of WHITE HAIRS below. Catipula year-round, PINK-PURPLE, 5-6 mm. Akenes 4-5 edged, hairs 4-5mm, white, fall early. Habitat: paddy field edges in lowlands. Anti-pyretic & tonic.',NULL);
INSERT INTO "NaturalObject" VALUES(2543,1,11,'28::18',1444,1940,'Cupid''s shaving brush','rau m l rau mung (N)::c chua l (S)','92::10::28::5::277','10/10/1998 09:29:29',NULL,'Ho (1970) v.2 p.525','30-70cm, light green, brittle, seldom branched. Leaves variably & SHALLOWLY LOBED (resemble to be widely serrate). Capitula few, bell-shaped, BRIGHT PINK; ONE ROW of green bracts; all flowers are tubular. Akenes 3.5mm, with FINE hairs. Habitat: gardens, grassland. Leaves consumed as vegetable, also for fever, cough, abcesses (external application).',NULL);
INSERT INTO "NaturalObject" VALUES(2544,1,11,'28::18',462,411,'','sai dat','92::10::100','06/10/1995 09:29:29',NULL,' ',' ',NULL);
