INSERT INTO "NaturalObject" VALUES(2580,1,145,'11::18',1461,96,'','di nga nc','92::95','07/09/1998 09:29:29',NULL,'Ho (1970) v.1 p.636','10 m. Trunk straight, wood reddish. Young branches with scaly hairs. Leaves of small trees may be single or with 3 pinnae, otherwise 9; ASYMMETRICAL; NOT HAIRY, 16 cm, broadly ovate; the top pinna often FUNNEL-SHAPED. Male flowers on compound racemes; females on PIKES axillary; 3 petals; 6 stamens. Capsules 1 to 3-chambered. Seeds large. Habitat: near water, at back of mangroves. Distr. Vietnam: Phu Quoc, Ha Tien, Saigon.',NULL);
INSERT INTO "NaturalObject" VALUES(2581,1,176,'11::18',1462,22,'','',362,'07/10/1995 09:29:29',NULL,' ','Leaves 5-foliate.',NULL);
