INSERT INTO "NaturalObject" VALUES(2589,1,194,'11::18',144,303,'elm, American','du M ?',103,'11/01/1900 09:29:29',NULL,' ','Distr. most northerly: Saskatchewan.',NULL);
INSERT INTO "NaturalObject" VALUES(2590,1,194,'11::18',144,67,'elm, Tonkinese ?','du ?','92::9::105','11/01/1900 09:29:29',NULL,' ','Large. Branchlets slender, somewhat hairy. Leaves tough, stiff, serrate, shiny above. Flowers bi-sexual, axillary, sessile, in whorls. Distr.: only on Ba Vi above 800 m.',NULL);
INSERT INTO "NaturalObject" VALUES(2591,1,194,'11::18',145,71,'','su','92::9::16::654','31/05/1999 09:29:29',NULL,'Forestry Inst. v.2','15-30m, deciduous. Runk cylindrical, bark gray-black, a bit rough. Crown spreading, young leaves fresh green. Branchlets hairy then smooth, with air spores. Leaves alternate, obovate: 3-8x2-5 cm, brownish hairs along veins on lower surface; UPPER HALF serrate; base asymmetrical; petioles 0.6-1 cm, hairy then smooth/ Flowers Dec-Jan (before young leaves), in 1-5 (often 3); 4 sepals obovate; 4 stamens. Drupe Aug-Sep, nearly globose, 4-5mm, gray-red then black, long-pediceled. Distr.: Laos, China, Vietnam: scattered northern C & N: Ba Vi, Ha Nam Ninh, Binh Tri Thien, Thuan Hai. Habitat: prefers sunlight, forest margins, roadsides or clear forests. Wood not good, for common appliances. Fruit skin medicinal.',NULL);
INSERT INTO "NaturalObject" VALUES(2592,1,194,'11::18',995,72,'','ngt lng','92::10::312','07/10/1995 09:29:29',NULL,' ','Wood good quality, long lasting, used in various purposes.',NULL);
