INSERT INTO "NaturalObject" VALUES(2594,1,67,'11::4',1418,1702,'','m qu','1235::5::111','08/10/1995 09:29:29',NULL,' ','Also combined with ''C. javanensis'' to be a single species ''Malura cochinchinensis''.',NULL);
