INSERT INTO "NaturalObject" VALUES(2599,1,68,28,940,67,'','day suoi','92::10::111','08/10/1995 09:29:29',NULL,' ','Resembles ''B. nivea''. Habitat: common along streams. Leaves cooked in a soup.',NULL);
