INSERT INTO "NaturalObject" VALUES(2602,1,134,'11::18',1470,67,'hickory, ?','may chau::sn hnh o','92::10::28::45::166','08/10/1995 09:29:29',NULL,' ','Large. Wood pink, heavy, used in house construction. Distr. Vietnam: high mountains Cao Lang, Ha Tuyen.',NULL);
INSERT INTO "NaturalObject" VALUES(2603,1,134,'11::18',1471,67,'','ci','92::10::34::339','08/10/1995 09:29:29',NULL,' ','Leaves deciduous, pinnate, serrate. Male and females in drooping catkins. Fruits winged. Leaves very poisonous, can be used as insecticide or fish poison. Habitat: common along streams.',NULL);
INSERT INTO "NaturalObject" VALUES(2604,1,134,'11::18',1471,1971,'transcaucasia','','92::9::105','08/10/1995 09:29:29',NULL,' ','Distr.: not known to exist in Vietnam.',NULL);
INSERT INTO "NaturalObject" VALUES(2605,1,160,'28::18',511,63,'pokeweed, ?','thng lc','92::9::100','08/10/1995 09:29:29',NULL,' ','Distr. Vietnam: introduced.',NULL);
INSERT INTO "NaturalObject" VALUES(2606,2,260,'2::12',1472,1972,'sturgeon, common','c tm ?','92::10::20::124','29/10/1998 09:29:29',NULL,' ','3+ m, 100 kg. Common in Mediterranean, N Atlantic. Swims up into rivers to breed on river beds. Meat delicacy, eggs made into caviar.',NULL);
INSERT INTO "NaturalObject" VALUES(2607,1,111,28,1473,76,'oyster plant','lin i','92::9::105','09/10/1995 09:29:29',NULL,' ','Origin: Mexico. Small, nearly without stem. Leaves small, arranged in a rosea near base; lamina nearly round at apex, light green with golden & silvery shines. Leaves falling from stem can develop into new plants. Inflorescence developed from axil, long, bearing 15-20 small, white flowers.',NULL);
INSERT INTO "NaturalObject" VALUES(2608,1,62,'28::25',1474,1973,'gourd, ivy','','92::10::28::305','11/10/1995 09:29:29',NULL,' ','Leaves 4-4.5 by 5-5.5 cm, stalked, alternate, glandular, PENTAGON-SHAPED except based deeply cordate. Flowers stalked, axillary; petals rhomboid, overlapped. Fruits 5-6 cm long, oblong, red when ripe. In Nepal young shoots and fruits taken as vegetable curry or fried, ripe fruits also eaten raw.',NULL);
INSERT INTO "NaturalObject" VALUES(2609,2,609,'23::15',1475,24,'whale, sperm','',103,'19/07/1997 09:29:29',NULL,'MacDonal Encycl.','Up to 18 m, 36 tons; females about 80% of length & 50-60% weight. Head ENORMOUS: at least 1/3 of entire body, filled for most part with reserve of sperm oil. Fat layer up to 30 cm, accounts for 1/3 of total weight. Dorsal fin is little more than a curved hump. In small pods with 2 large males, several females. Feeds on mollusks. Traces of wound often seen on head, due to fighting with giant octopus or squid. Gestation 16 mo. Sexual maturity at 9 yrs for males, 25 yrs for females. Distr: worldwide between latitudes 70N and 70S; migrates between cold & warm areas according to season. Habitat: coastal & deep waters, rarely farther than 480 km from shore.',NULL);
INSERT INTO "NaturalObject" VALUES(2610,1,26,'28::29',1476,12,'strawberry, mock','','92::10::28::286','11/10/1995 09:29:29',NULL,' ','Internodes long. Leaves stalked, stipulate, alternate, 3-foliate; leaflets 1-2.5 by 1-2 cm, ovate, sessile, serrate, middle one larger than the rest, hairy. Flowers stalked, solitary, 1.3 cm across, YELLOW. Compound fruits globose, red, succulent. Distr. Nepal: common. Habitat: moist soils. Ripe fruits eaten fresh.',NULL);
INSERT INTO "NaturalObject" VALUES(2611,1,161,'11::18',12,1974,'pine, Dalat five-leaved','thng nm l','92::1248::23::284','23/02/1997 09:29:29',NULL,'Vietnam Red Book (96)','Identified 1960. 20-40 m, average diameter 60-80 cm but can be up to 2 m. Bark brown-red, rough, fissured or peels off. Branches relatrively slender, very short, brown and hairy then light grey and smooth. Buds cone-shaped, brown, formed by triangular scales with sharp points. Leaves 4-10 cm by 3 mm, trianguler in cross section. Male cones crowded near terminals, 5.5-10 by 2.5-3.5 cm, covered by brown scales. Regenerates poorly. Distr: THua Thien to Lam Dong, Kon Tum. Habitat: 1600-2500 m. Wood slightly hard, light, rot-resistant, easy to process.',NULL);
INSERT INTO "NaturalObject" VALUES(2612,1,370,'11::18',1477,1975,'','m tai bo','92::37::384','17/06/1999 09:29:29',NULL,'Forestry Inst. v.5','15-25 m. Trunk cylindrical, straight, not winged. Bark light grey, smooth. Crown light, spreading. Twigs slender, crooked, smooth. Flowers fewer in axillary whorls; unisexual, monoecious; bracts 6-10; calyx fused into hat shape; petals 4, fused below. Male flowers with 4 stamens fused below into tube as long as petals. Fruits SPECIFIC: egg-shaped, black, shiny, 2.5 by 1.8-2.2 cm, lower one-third covered by persistent RED calyx with MARGIN EXPANDED, HAT-SHAPED, fleshy, red, making tree very conspicuous during fruiting season. Seeds white, oily, poisonous. Distr.: Laos, Vietnam: Quang Nam, high central, Nghia Binh. Habitat: seondary forests 200-800m, often on soil mountain slopes. Flowers & fruits on tree same time during Feb-May. Crown beautiful, can be for shade.',NULL);
INSERT INTO "NaturalObject" VALUES(2613,1,136,'11::18',1478,1976,'','bi nhi l bu dc','92::10::45::587','27/06/1999 09:29:29',NULL,'Forestry Inst. v.6','Not in Ho (1970). 30 m, diam 60 cm. Evergreen, trunk erect. Bark brown-grey, smooth. Buds scale-like, with rust-colored hairs. Leaves 7-10 by 3.5-6 cm, alternate, leathery, entire, ELLIPTIC, ASYMMETRICAL, tip suddenly acuminate, base gradually tapered; 3 veins at base, mid-rib conspicuous 2 sides, lateral veins 2-3 pairs, conspicuous underneath. Compound umbels Jun-Jul (sometimes Dec), dioecious; flowers few in each terminal or axillary bundle, REDDISH; females with 6 sterile stamens. Berries in drupe form Nov (sometimes Apr-May), OVOID, 1.3-1.8 by 1-1.3 cm, brown-red when ripe. Seeds oily. Wood fragrant even dry, beautiful. Distr.: China, Vietnam: quite common but seldom in pure stands. Habitat: evergreen tropical forests 400-900 m, often forest margins. Grows slowly. Wood fragrant, for high-priced furniture, shingles, decors...',NULL);
INSERT INTO "NaturalObject" VALUES(2614,1,136,'11::18',1478,1561,'','bi nhi l thun','92::10::45::322','27/06/1999 09:29:29',NULL,'Forestry Inst. v.6','Not in Ho (1970). 20 m, evergreen. Bark brown-red, smooth. Branchelets with yellow-grey hairs. Leaves 4-11 by 0.8-2 cm, crowded towards terminals, simple, acuminate 2 ends, bright green above, light green or grey-green underneath, smooth; lateral veins 5-6 pairs, very fine; petioles 1 cm, with long grey hairs when young. Compound umbels May-Jun (sometimes Nov), dioecious, 10 in each axillary, RUSTY YELLOW. Berries in drupe form Oct (sometimes Jan-Feb), GLOBOSE, 0.8-1 cm, with calyx persistene, erect, 4-angle star-shaped, brown-black when ripe. Seeds oily. Habitat: scattered, common evergreen forests 500-800 m, sometimes nearly predominant on upper hill slopes. Distr.: Hainan, Vietnam: Nghe Tinh, Binh Tri Thien.',NULL);
INSERT INTO "NaturalObject" VALUES(2615,1,136,'11::18',1478,1115,'','bi nhi l t','92::10::45::85::166','12/10/1995 09:29:29',NULL,' ','Not in Ho (1970). 25 m, evergreen. Back grey-black, smoothish. Branchelets NOT HAIRY. Leaves 5.5-8.5 by 1.6-3.2 cm, long elliptic or obovate, crowded towards terminals, apex OBTUSE, green above, grey-green below, smooth 2 sides; lateral veins 2-3 pairs, very fine; petioles 1.5 cm, NOT HAIRY. Flowers dioecious, 3-5 in each bundle, YELLOWISH. Berries GLOBOSE, 8 mm, with ROUND persistent calyx. Regenerate well: many young trees around parent tree. Distr.: Haina, Vietnam: Binh Tri Thien. Habitat: evergreen trees 400-600 m. Wood quite beautiful, rot=resistant, for building ships, irrigation works...',NULL);
INSERT INTO "NaturalObject" VALUES(2616,1,140,'11::18',76,1962,'','bng lng hoa ',103,'27/06/1999 09:29:29',NULL,'Forestry Inst. v.6','Not in Ho (1970). 20-30 m. Bark thin, light yellow, peels off in scales. Leaves 6-16 by 3-6 cm, opposite or sub-opposite, apex acuminate, base obtuse, green above, paler below, 2 sides hairy then smooth; lateral veins 6-10 pairs, small, nearly parallel; petioles 4-5 mm, with grey-brown hairs. Panicles Aug-Oct, terminal, peduncles full of yellow-brown hairs; flowers quite large, calyx bell-shaped, 5-6 teeth, hairy; petals 6, REDDISH, round, long stalked; stamens 60-70. Fruits ovoid, 1.2-1.5 cm, when ripe black & dehisce into 6. Distr.: China, Vietnam: N, high plateau. Habitat: semi-deciduous forests, usually in smal stands, orefers sunlight: near forest margins or cleared forests. No use known. Good as roadside or garden ornament.',NULL);
INSERT INTO "NaturalObject" VALUES(2617,1,125,'11::18',70,1118,'','d i Loan','92::111','12/10/1995 09:29:29',NULL,' ','25 m, avergreen. Bark grey-brown, rough, with vertical fissures. Leaves 7-11 by 3-4 cm, resemble those of ''Millenia indica'': sharply & widely serrate except near base, light green above, grey-green below; lateral veins near perfectly parallel. Flowers unisexual, monoecious; in erect inflorescences. Males flowers in 3; female solitary or in 2. Fruits on long, strong peduncle, sessile, 2-3 cm, with branched hairs VERY SHARP. Distr. China, VietnamL Binh Tri Thien, Phu Khanh. Habitat: tropical evergreen, semi-evergreen, or rain forests under 300 m. Seeds edible.',NULL);
INSERT INTO "NaturalObject" VALUES(2618,2,327,'14::20',147,1977,'snake, flying::tree, golden tree','',103,'15/08/1999 09:29:29',NULL,'Cox et al. (1998)','1.3-1.6 m. Head green with black markings (resembling lizards); chin & supralabials ivory. Body green or greenish-yellow with EACH SCALE BLACK-EDGED; markings more dense on dorsal surface where they usually form distict crossbars. Ventrals green with black spots next to each lateral notch. Body scales smooth, number 17 at mid body. Diurnal. Diet: lizards, rodents, other snakes. Litter size 6-12; hatchlings 15-20 cm, resemble adults but color and pattern brighter. Distr.: most widespread of the genus in SE Asia: Thailand nationwide, Indochina, Indonesia, S. China, Sri Lanka, Assam, Bengal. Habitat: plains and hills to 550m.',NULL);
INSERT INTO "NaturalObject" VALUES(2619,2,327,'14::20',147,403,'snake, paradise tree','',103,'15/08/1999 09:29:29',NULL,'Cox et al. (1998)','1.2 m. Ground color black with red & green markings. Head black, crossed by five narrow, dotted greenish-yellow bands plus blotches of same color. Bands become large, greenish-yellow blotches on neck; ocassionally reddish-orange color. Blotches succeeded by red or reddish-orange spots on body; smaller, light-green dots on tail. Remaining body scales black with a green spot in each center, giving the body sides a speckled appearance. Body scales sometimes faintly keeled, number 17 at mid body. Litter size 5-8. Diurnal, arboreal. Distr.: rare SE Asia; Thailand S. Habitat: forests to 1500m.',NULL);
INSERT INTO "NaturalObject" VALUES(2620,2,548,'14::13',1479,1978,'snake, banded coral','rn ?','92::339','12/12/1995 09:29:29',NULL,' ','60 cm. Brownish with a red or orange dorsal stripe enclosed between 2 black stripes. Situated low on lateral surfaces are pale or white stripes which are again enclosed within 2 black stripes. Ventral surface is barred black and white with exception of the tail, which is barred black & red. Has a number of recognized sub-species. Distr.: one of 2 ''Maticora'' species in SE Asia to Philippines.',NULL);
INSERT INTO "NaturalObject" VALUES(2621,2,548,'14::13',1479,1979,'snake, long-glanded coral','rn ?','92::339','15/08/1999 09:29:29',NULL,' ','Up to 150 cm. Dark blue or blackish with a pale band on each side of body and a dark stripe running below the paler markings. Head and ventral surface BRIGHT CORAL RED. Distr.: one of 2 ''Maticora'' species in SE Asia to Philippines.',NULL);
INSERT INTO "NaturalObject" VALUES(2622,2,548,'14::13',149,1980,'snake, small-spotted coral','rn v nng','92::339','28/08/1999 09:29:29',NULL,'Cox et al. (1998); Kien & Thang (1995)','50 cm. Slender. Variable. Head brownish-black. Body uniformly dark orange or reddish-brown or light brown. Body pattern of widely separated dots on flanks, or round black dots distributed sparsely, or a black twisted segmented pattern on vertebral ridge, a black vertebral stripe, or no pattern at all. A black band encircles the base of tail, another at tail tip. Subcaudals black & white in varrying proportion. Upper head has a Y-shaped broad black marking. Vietnam: Back mid- to dark brown, some with black blotcheds; sides possibly with small blotches; head dark brown or black above; lips and chin yellow; belly pink or red; tail with 2 black rings, grey blotches below. Nocturnal. Litter size 2. Diet: small snakes (particularly ''Typhlops''), lizards, often dig soil for preys. Distr. throughout SE Asia, but rare in many parts of its range; Vietnam: S only. Habitat: under debris, logs, similar cover in forests to 1000m. Venomous and dangerous.',NULL);
INSERT INTO "NaturalObject" VALUES(2623,2,548,'14::13',149,1981,'snake, McClelland''s coral','rn xe iu','92::339','28/08/1999 09:29:29',NULL,'Cox et al. (1998); Kien & Thang (1995)','Up to 90 cm - one of the largest of the genus. Also has greatest variety of coloration. Body slender, cylindrical. Head not distinct, black; behind eyes is a broad ivory band followed by a broad black band. Ground color usually red-brown with 24-40 on back and 2-6 on tail narrow, black, light-edged bands that vary in intensity, some incomplete on flanks. A BROAD WHITE RING on head. Some may be blotched or else have a continuous vertebral stripe. Ventral surface usually yellowish, black bands continue across the belly, also scattered dark brown or black spots on belly. Nocturnal. Litter size 6-14 eggs, Jul-Aug in N Vietnam. Diet: lizards, other snakes. Distr.: wide NE India, Nepal, SE Asia (except Malaysia), Taiwan, Thailand N&E, Philippines; Vietnam: nationwide. Habitat: Lowlands to above 1000m, usually found under forest litter or fallen logs. Venomous but no case of human fatality.',NULL);
INSERT INTO "NaturalObject" VALUES(2624,2,548,'14::13',150,1982,'krait, red-headed','rn cp nong u vng','92::339','11/08/1999 09:29:29',NULL,'Cox et al. (1998); Kien & Thang (1995)','Average 1.5m, sometimes 2m. Very striking in appearance: BRIGHT RED head & tail, stocky shiny body black with a pale bluish white stripe on lateral surfaces or white specks along vertebral ridge and sides of body. Different description for Vietnamese species (different subspecies?): black back; a red or yellow stripe on vertebral ridge; may be a red, orange or yellow stripe along sides; head & neck up to 6th scale on belly YELLOW; yellow or orange below. Hatchlings unrecorded. Diet: snakes, skinks. Distr. somewhat rare: S Burma, Malaysia, Vietnam, Indonesia. Habitat: from sea level to mountain forests 900m. Venomous and potentially fatal.',NULL);
INSERT INTO "NaturalObject" VALUES(2625,2,250,'14::13',957,1983,'viper, Russell''s','','92::37::94','15/08/1999 09:29:29',NULL,'Cox et al. (1998)','Robust, large, up to 150 cm. Coloration variable: olive, brown, yellowish, buff, with a series of dark oval rings (may be rimmed with thin white lines) on dorsal and lateral parts. Terrestrial, largely nocturnal, feeds mainly on small mammals. When threatened, makes a very loud, continuous hissing noise that can be quite alaerming. Much feared, figures high on the list of the world''s dangerous snakes: short-tempered, will strike without hesitation if trodden on. Ovoviviparous, litter up to 63; hachlings 24-30 cm resemble adults. Distr.: E Pakistan, across India into SE Asia. Has a patchy local distribution, but where it occurs it tends to be very common. Habitat: open country to 2000m, avoids dense vegetation, often coiled in open shelters, may even enter well-lit houses. Venomous and potentially fatal.',NULL);
INSERT INTO "NaturalObject" VALUES(2626,2,329,'14::20',152,1984,'viper, pit Wagler''s::viper, temple','','92::37::95','10/08/1999 09:29:29',NULL,'Cox et al. (1998)','1 m. Handsome: rich green with bright yellow CROSSBANDS, but there is great deal of variation, some almost uniformly green; others white, red, dark green or black spots or crossbands. Head VERY LARGE, very distinct from neck, head top is black with green markings. Ventral shields white with black edges. Tail BLACK. Litter size upto to 41. Juveniles: yellowish-green with red and white spots or brownish crossbands. Nocturnal, arboreal. Distr.: SE Asia. Habitat: common among low vegetation lowlands from mangroves to 1200m, also near human dwellings. Venomous and dangerous.',NULL);
INSERT INTO "NaturalObject" VALUES(2627,2,329,'14::20',152,1985,'viper, mangrove pit-::viper, shore pit-','','92::37::95','10/08/1999 09:29:29',NULL,'Cox et al. (1998)','Average 80 cm, sometimes 120 cm. Relatively stout body bears strongly keeled scales. Uniform purplish brown or grey; or greenish-yellow  with greenish spots & dark saddle-like MARKINGS on dorsal surface, wide above and narrower on the side. Ventral scales white with black edges. Head large, chunky looking, very distinct from neck. Head scales tuberculate or keeled, crown blackish-brown. Alert, vicious, will strike blindly & quickly at anything that moves. Litter size 7-14; young 24 cm. Diet: lizards, frogs, other small animals. Nocturnal. Distr.: abundant from E Bengal, S Burma, Malaysia, Sumatra. Habitat: essentially terrestrial but also commonly encountered in low bushes, common in mangroves. Venomous and dangerous.',NULL);
INSERT INTO "NaturalObject" VALUES(2628,2,329,'14::20',152,1986,'viper, Pope''s pit-','rn lc xanh min Nam::rn lc u v','92::339','29/08/1999 09:29:29',NULL,'Cox et al. (1998); Kien & Thang (1995)','90 cm. Very similar to ''T. albolabris'' and, but distinguished by penis WITHOUT SPINE, the pale lateral line either ABSENT or INDICTINCT in adults, and different geographical area. Top of head and body medium green, ventral greenish. Tail reddish-brown. Head scales smooth, body scales keeled. Diet: birds, lizards, frogs. Nocturnal. Litter size 7-12; young 12-18 cm. Not very hot-tempered, often not bite when approached but may suddenly raises body and opens mouth ti threat, then bites. Distr.: SE Asia wide from Assam to Burma, Cambodia, Malaysia, Indonesia; Vietnam: S Only. Habitat: most abundant low shrubs in hilly terrains 900-1500m. Venomous but may not be dangerous as dangerous as ''T. stejnegeri''.',NULL);
INSERT INTO "NaturalObject" VALUES(2629,2,327,'14::13',156,1987,'snake, yellow-ringed cat::snake, mangrove','rn ro ?','92::37::95','13/08/1999 09:29:29',NULL,' ','Up to 2.4 m. Strikingly marked with 40-50 VIVID YELLOW BANDS (some meet on back, while in some individuals restricted to lateral surface) on a GLOSSY BLACK ground color. Underneath of head may be vividly yellow. Body notably laterally compressed. Distr.: SE Asia. Habitat: lowland forests & mangroves. Ventral surface leaden grey. Scales smooth, 21-23 at mid body. Highly arboreal. Food: lizards, birds, bats, other snakes.',NULL);
INSERT INTO "NaturalObject" VALUES(2630,2,329,'14::20',152,1988,'viper, Chinese green tree::viper, bamboo pit-','rn lc xanh::rn lc u ','92::37::1146','29/08/1999 09:29:29',NULL,'Cox et al. (1998); Kien & Thang (1995)','50-110 cm. Externally identical to ''T. popeiorum'', but distinguished by penis WITH SPINE (so only males can be identified with certainty, the pale lateral line MORE DICTINCT, and different geographical area but its hemipenis has a different structure. Head distinct. Body selnder at neck, gradually enlarged towards tail, may be much brighter green than other species of same genus. Green above, yellow to greenish below. A white or yellowish lateral line edged with orange or reddish in males. Eyes usually ORANGE or PINK. The red at the tail end is PALE. Diet: mainly frogs, also small mammals, birds, lizards. Litter size 3-12; newborn resemble adults. Nocturnal and semi-arboreal: under foliage, sometimes descends to near water, in grasses. Distr.: C and SE China, Myanmar, Thailand: isolated, reported in Cambodia; Vietnam: N&C common. Habitat: montane forests to 2,800m, in dense bushes or wastelands. Hot temper, often stays quietly in trees then bites violently when disturbed. People harvesting forest products are common victims. Venomous and dangerous.',NULL);
INSERT INTO "NaturalObject" VALUES(2631,2,329,'14::13',152,1989,'viper, white-lipped tree::viper, white-lipped pit-','rn lc xanh mi trng::rn h lc (N)','92::37::78::94','29/08/1999 09:29:29',NULL,'Cox et al. (1998); Kien & Thang (1995)','60-100 cm. Head triangular, distinct. Body fairly stout, PALE GREEN above, greenish below; a white stripes at side (absent or indistinct in females). Scales keeled. Upper lip GREENISH, YELLOW OR WHITE. Chin, throat and belly greenish or yellowish-white; tail end DARK RED. Eyes usually yellowish. Hunts mice, birds, lizards and frogs at night on the ground, spends daytime resting in vegetation. Litter 7-16 miniature replicas of adults, 12-18 cm long; male often present nearby. Slow but seldom evades when approached, ready to bite when incited. Distr.: E India to SE China and SE Asia; Thailand: nationwide; Vietnam: nationwide. Habitat: prefers open country below 400m, in trees 2-3 m high or bushes, sometimes inhabits urban areas and gardens; seldom terrestrial. Venomous and dangerous, responsible for many snake-bite cases but seldom fatal.',NULL);
INSERT INTO "NaturalObject" VALUES(2632,2,328,'14::15',1480,1383,'','n vy u phn','92::339','31/08/1999 09:29:29',NULL,'Kien & Thang (1995)','0.9-1m. Body stout, largest part not as 3 times larger than neck. Head relatively broad. Dorsal scales very small, slightly keeled. Ventral scales small but distinct from other scales. Between large scales on head there are SMALL SCALES. Yellow above, with 35-45 gray stripes extending to mid-sides, broader than the space between. Distr.: Malaysia to Java and Vietnam: common Vung Tau.',NULL);
INSERT INTO "NaturalObject" VALUES(2633,2,548,'14::13',150,1990,'krait, many-banded','rn cp nia (N)::rn mi gm bc (S)','92::37::80::1146','15/08/1999 09:29:29',NULL,' ','90 cm. Body jet black with 42-60 white or yellowish crossbands (24-27 in ''B. candidus''). Distr. S China, Taiwan. Habitat: near water particularly in wooded areas, also common in paddy fields.',NULL);
INSERT INTO "NaturalObject" VALUES(2634,2,327,'14::12',1481,1991,'snake, tentacled::snake, fishing','rn ?',103,'14/08/1999 09:29:29',NULL,'Cox et al. (1998)','70 cm. The only species of the genus. Equipped with a pair of fleshy PROTRUBERANCES (tentacle-like appendages) which extend from the snout, used as a lure to catch fish. Head distinct; body scales strongly keeled; ventrals very narrow. Color & pattern vary; markings in shades of brown most common making mottled appearance. Ventral surface brownish-yellow. Docile. Litter size 5-13, hatchling 20-24 cm, resemble adults. Distr. Thailand C&S, Cambodia, Vietnam. Habitat: still, acidic freshwater bodies; the most aquatic of the sub-family ''Homalopsinae'', almost helpless in land.',NULL);
INSERT INTO "NaturalObject" VALUES(2635,2,327,'14::12',154,1992,'snake, plumbeous::snake, ywllowbelly water','rn nc ?',103,'14/08/1999 09:29:29',NULL,'Cox et al. (1998)','40-56 cm. Dark brown or greyish-olive or slaty grey above, changing abruptly to yellow low on flanks. Dark spots on dorsal surface. Ventral surface dull white or pale yellow with black dots medially; subcaudals with a median black line. Snout broadly rounded; head indistinct. Body cylindrical to oval, short, stout; scales smooth. Litter of 6-30, each 8-13 cm, with adult coloration. Distr.: Burma, S China, wide SE Asia. Habitat: near freshwater to 1200m.',NULL);
INSERT INTO "NaturalObject" VALUES(2636,2,327,'14::12',154,1651,'snake, Bocourt''s water','rn nc ?','92::104','14/08/1999 09:29:29',NULL,'Cox et al. (1998)','120 cm; largest in the genus in SE Asia. Body stout, cylindrical, scales smooth. Dark brown above with narrow irregular yellowish crossbars and spots which sometimes form irregular series of line along body. Lower sides yellow, with dark dorsal color sometimes extending across belly. Head distinct, dark with yellow marks above, yellow below; pupil vertically elliptical. Litter 6-17; young 22cm, resemble adults. Distr.: Thailand, Malaysia Peninsualr, Cambodia, Vietnam. Habitat: well-watered lowlands, coasts.',NULL);
INSERT INTO "NaturalObject" VALUES(2637,2,327,'14::20',155,1993,'snake, Malayan green whip','rn ?','92::339','13/08/1999 09:29:29',NULL,'Cox et al. (1998)','1m. Very similar to ''A. prasinus'', but differs in possessing a single anal shield and fewer ventral (186-195) and subcaudal (115-156) scales. The least known of whip snakes in SE Asia. Body bright green or bluish-green with pale linear markings, sometimes brown or grey above, white and sometimes with a green median line below. Ventral color usually agrees with dorsal color. A white line extends along the outer edge of the ventral scales throughout the body length. Arboreal. Active during the day. Diet: cold-blooded animals. Distr.: Thailand S, Malaysia Peninsula, Java. Habitat: forested foothills.',NULL);
INSERT INTO "NaturalObject" VALUES(2638,2,327,'14::20',155,1994,'snake, Oriental whip','rn ?','92::37::130','13/08/1999 09:29:29',NULL,'Cox et al. (1998)','1.9 m. Very similar to ''A. mycterizans'', but anal shield is divided and has more ventral (194-235) and subcaudal (151-235) scales. The largest and most common whip snakes in SE Asia. Bright to dark green above, pale green below with a thin white line along the edges of ventrals, but coloration may vary from grey to orange and brown with pale linear markings. Diurnal. Diet: mostly lizards. Litter size 4-10; newborn about 35 cm, resemble adults. Distr.: throughout SE Asia. Habitat: forests to 2100m.',NULL);
INSERT INTO "NaturalObject" VALUES(2639,2,327,'14::20',156,1995,'snake, black-headed cat::snake, red cat','rn ro ?','92::339','13/08/1999 09:29:29',NULL,'Cox et al. (1998)','1.7 m. Body color varies from grey- or reddish-brown to intense bright red. Head usually dark brownish-red to olive. Occasionally have small black dots on flanks. Labials creamy white; ventral pink or dark yellow. Diet: lizards, birds, mice, probably frogs and other snakes. Distr.: Thailand S, Malaysia, Borneo, Java. Habitat: humid, well-watered forested foothills to 1100m.',NULL);
INSERT INTO "NaturalObject" VALUES(2640,2,327,'14::20',156,1996,'snake, brown tree','rn ?','92::339','13/12/1995 09:29:29',NULL,' ','200 cm. Reddish brown with dark crossbars, ventral surface cream or pinkish. Scales smooth, many body scales edged with black; body scales number 19-23 at mid body. Distr.: Papua New Guinea, Philippines, Indonesia.',NULL);
INSERT INTO "NaturalObject" VALUES(2641,1,117,'28::25',234,1997,'','c nu','92::9::10::884','13/12/1995 09:29:29',NULL,' ','Not in Ho (1970). Also occurs in wild. Stems cylindrical, with thorns near base. Leaves alternate near base, opposite near terminals, cordate but may look line broadly ovate; 5 veins from base. Inflorescence large, flowers 16-20. Fruits 3-winged. Seeds winged. Tubers rough, bark grey-brown, meat brown-red or yellow, contains much tannin.',NULL);
INSERT INTO "NaturalObject" VALUES(2642,1,117,'28::25',234,708,'','su van::t nm l','92::111','11/10/1998 09:29:29',NULL,'Ho (1970) v.2 p.611','Leave 3- or 5-petiolate, apart from each other, long stalk; leaflets asymmetrical, broadly ovate, short-petioled, hairy below. Inflorescence axillary, long, male spikes hairy; female downy. Capsules 2cm, wing 6mm. Seeds with brown wing. Tuber branched, rough outside, edible boiled.',NULL);
INSERT INTO "NaturalObject" VALUES(2643,1,117,'28::25',234,1998,'','khoai mi','92::111','11/10/1998 09:29:29',NULL,'Ho (1970) v.2 p.613','Could be confused with ''D. alata''. Tubes long, deep in soil. Leaves alternate near base, opposite higher; base deeply cordate. Spikes crooked back and forth; flowers small. Capsules broader than long. Seeds with brown wing. Tubers edible. Distr. Vietnam: Phu Quoc, Dong Nai, Khanh Hoa. Habitat: evergreen forests.',NULL);
INSERT INTO "NaturalObject" VALUES(2644,1,32,'11::4',329,1274,'sage','xn ','92::9::105','13/12/1995 09:29:29',NULL,' ','Flowers red, beautiful. Var. ''purpurea'': flowers deep purple.',NULL);
INSERT INTO "NaturalObject" VALUES(2645,1,32,'11::4',329,1999,'','dan sam','92::9::100','13/12/1995 09:29:29',NULL,' ','Tubers used as tonic esp. for women.',NULL);
